import React from "react";
import { Card, Button } from "react-bootstrap";
import StatusList from "../todostatus";
import PriorityList from "../todopriority";

const TodoCard = ({
  todo,
  handleonclickp,
  handleondelete,
  parteditonclick,
}) => {
  const handleonclick = () => {
    handleonclickp(todo);
  };

  const handleonclickdelete = () => {
    handleondelete(todo);
  };

  return (
    <div className="col-md-4">
      <div className="card card-01">
        <div className="profile-box">
          <img
            className="card-img-top rounded-circle"
            src="https://via.placeholder.com/150" // Placeholder image, replace with dynamic content if available
            alt="Card image cap"
          />
        </div>
        <div className="card-body text-center">
          <span className="badge-box">
            <i className="fa fa-check"></i>
          </span>
          <h4 className="card-title">{todo.title}</h4>
          <p className="card-text">{todo.description}</p>
          <StatusList todo={todo} update={parteditonclick} />
          <PriorityList todo={todo} update={parteditonclick} />
          <span className="social-box">
            <Button onClick={handleonclick} variant="default">
              Edit
            </Button>
            <Button onClick={handleonclickdelete} variant="default">
              Delete
            </Button>
          </span>
        </div>
      </div>
    </div>
  );
};

export default TodoCard;
