import React from "react";
import TodoForm from "./TodoForm";
import TodoCard from "./TodoCard";

// Color variables
const backgroundColor1 = "#6F8D8A";
const textColor1 = "#2a403d";

const Home = ({ todos, handleSubmit, handleonclick, handleondelete, parteditonclick, edit, todo, editonclick }) => {
  return (
    <>
      {!edit ? (
        <div
          className="home-container container-fluid"
          style={{ backgroundColor: backgroundColor1, color: textColor1 }}
        >
          <h1 className="text-center my-4">Todo List</h1>
          <div className="form-container">
            <TodoForm onSubmit={handleSubmit} />
          </div>
          <div className="list-container row">
            {todos.length > 0 ? (
              todos.map((todo, index) => (
                <TodoCard
                  key={index}
                  todo={todo}
                  handleonclickp={handleonclick}
                  handleondelete={handleondelete}
                  parteditonclick={parteditonclick}
                />
              ))
            ) : (
              <p className="text-center">No tasks available.</p>
            )}
          </div>
        </div>
      ) : (
        <EditForm edit={true} todo={todo} editclick={editonclick} />
      )}
    </>
  );
};

export default Home;
